import  React , { Component } from "react";

class PokemonComponent extends Component {
  render() {
    const {lista} = this.props; 
    return (
      lista.map((pokemon) => <div>
        <p>{pokemon.name}</p>
        <img src={pokemon.image} alt={pokemon.name}/>
      </div>)
    )
  }
}

export default PokemonComponent;
