import React, { Component } from "react";
import './App.css';

function handleClickVamo() {
  console.log('Vamo');
}

function handleClickQue() {
  console.log('que');
}

function handleClickBora() {
  console.log('bora')
}

const tentar = [handleClickVamo, handleClickQue, handleClickBora]


class App extends Component {
  render() {
  return <div>
    {tentar.map((item) => <button key = {item()} onClick={item}>clica e descubra</button>)}
  </div>
  }
}

export default App;
