import React from 'react';
import './App.css';

function handleClick(texto) {
  console.log('Oi tudo bem');
}

class App extends React.Component {
  render() {
    const textoBotao = ['Oi', 'tudo', 'bem'];
    return (
      textoBotao.map((texto) => <button key={texto} onClick={handleClick}>{texto}</button>)
    )
  }
}

export default App;
