import React from 'react';
import './App.css';

class App extends React.Component {

  state = {
    numClickBtUm: 0,
    numClickBtDois: 0,
    numClickBtTres: 0,
  }

  handleClickUm = () => {
    this.setState((prevState, ...props) => ({
      numClickBtUm: prevState.numClickBtUm +1,
    }))
  };

  handleClickDois = () => {
    this.setState((prevState, ...props) => ({
      numClickBtDois: prevState.numClickBtDois +1,
    }))
  };

  handleClickTres = () => {
    this.setState((prevState, ...props) => ({
      numClickBtTres: prevState.numClickBtTres +1,
    }))
  };

  render() {
    const {state: {numClickBtUm, numClickBtDois, numClickBtTres},
    handleClickUm, handleClickDois, handleClickTres} = this;
    return (
      <div>
        <button 
          onClick={handleClickUm}
          style={{backgroundColor: 44444,}}
        >
          {numClickBtUm}
        </button>
        <button onClick={handleClickDois}>{numClickBtDois}</button>
        <button onClick={handleClickTres}>{numClickBtTres}</button>
      </div>
    )
  }
}

export default App;
