import React from 'react';
import './App.css';

class App extends React.Component {

  state = {
    numClickBtUm: 0,
    numClickBtDois: 0,
    numClickBtTres: 0,
  }

  handleClickUm = () => {
    this.setState((prevState, ...props) => ({
      numClickBtUm: prevState.numClickBtUm +1,
    }))
  };

  handleClickDois = () => {
    console.log('tudo');
  };

  handleClickTres = () => {
    console.log('bem')
  };

  render() {
    return (
      <div>
      <button onClick={this.handleClickUm}>{this.state.numClickBtUm}</button>
      <button onClick={this.handleClickDois}>tudo</button>
      <button onClick={this.handleClickTres}>bem</button>
      </div>
    )
  }
}

export default App;
